"package linkedlist

import (
	"reflect"
	"testing"
)

func fillList(list *Cyclic, n int) {
	for i := 1; i <= n; i++ {
		list.Add(i)
	}
}

func TestAdd(t *testing.T) {
	list := NewCyclic()
	fillList(list, 3)

	want := []interface{}{1, 2, 3}
	var got []interface{}
	var start *Node
	start = list.Head

	for i := 0; i < list.Size; i++ {
		got = append(got, start.Val)
		start = start.Next
	}
	if !reflect.DeepEqual(got, want) {
		t.Errorf("got: %v, want: %v", got, want)
	}
}

func TestWalk(t *testing.T) {
	list := NewCyclic()
	fillList(list, 3)

	want := 1
	got := list.Walk()

	if got.Val != want {
		t.Errorf("got: %v, want: nil", got)
	}
}

func TestRotate(t *testing.T) {
	type testCase struct {
		param        int
		wantToReturn int
	}
	list := NewCyclic()
	fillList(list, 3)

	testCases := []testCase{
		{1, 2},
		{3, 2},
		{6, 2},
		{7, 3},
		{-2, 1},
		{5, 3},
		{8, 2},
		{-8, 3},
	}
	for idx, tCase := range testCases {
		list.Rotate(tCase.param)
		got := list.Head.Val
		if got != tCase.wantToReturn {
			t.Errorf("got: %v, want: %v for test id %v", got, tCase.wantToReturn, idx)
		}
	}
}

func TestDelete(t *testing.T) {
	list := NewCyclic()
	fillList(list, 3)

	want := 2
	wantSize := 2
	list.Delete()
	got := list.Head.Val
	if want != got {
		t.Errorf("got: %v, want: %v", got, want)
	}
	if wantSize != list.Size {
		t.Errorf("got: %v, want: %v", got, want)
	}
}

func TestDestroy(t *testing.T) {
	list := NewCyclic()
	fillList(list, 3)
	wantSize := 0
	list.Destroy()

	got := list.Head

	if got != nil {
		t.Errorf("got: %v, want: nil", got)
	}

	if wantSize != list.Size {
		t.Errorf("got: %v, want: %v", got, wantSize)
	}
}

func TestJosephusProblem(t *testing.T) {
	type testCase struct {
		param        int
		wantToReturn int
		listCount    int
	}

	testCases := []testCase{
		{5, 4, 8},
		{3, 8, 8},
		{8, 5, 8},
		{8, 5, 8},
		{2, 14, 14},
		{13, 56, 58},
		{7, 5, 5},
	}

	for _, tCase := range testCases {
		list := NewCyclic()
		fillList(list, tCase.listCount)
		got := JosephusProblem(list, tCase.param)
		if got != tCase.wantToReturn {
			t.Errorf("got: %v, want: %v", got, tCase.wantToReturn)
		}
	}
}
