"package trie

import (
	"fmt"
	"math/rand"
	"testing"
)

// CAUTION : make sure to limit the benchamrks to 3000 iterations,
// or removal will mostly process an empty Trie, giving absurd results.

// go test -v -bench=. -benchmem -benchtime=3000x

// === RUN   TestTrieInsert
// --- PASS: TestTrieInsert (0.00s)
// === RUN   TestTrieRemove
// --- PASS: TestTrieRemove (0.00s)
// === RUN   ExampleNode
// --- PASS: ExampleNode (0.00s)
// goos: linux
// goarch: amd64
// pkg: github.com/TheAlgorithms/Go/structure/trie
// BenchmarkTrie_Insert
// BenchmarkTrie_Insert-8                      3000           1559881 ns/op         1370334 B/op      25794 allocs/op
// BenchmarkTrie_Find_non_existant
// BenchmarkTrie_Find_non_existant-8           3000                59.1 ns/op             0 B/op          0 allocs/op
// BenchmarkTrie_Find_existant
// BenchmarkTrie_Find_existant-8               3000               238 ns/op               0 B/op          0 allocs/op
// BenchmarkTrie_Remove_lazy
// BenchmarkTrie_Remove_lazy-8                 3000               126 ns/op               0 B/op          0 allocs/op
// BenchmarkTrie_Remove_and_Compact
// BenchmarkTrie_Remove_and_Compact-8          3000            213945 ns/op               0 B/op          0 allocs/op
// PASS
// ok      github.com/TheAlgorithms/Go/structure/trie      5.355s

func BenchmarkTrie_Insert(b *testing.B) {

	// prepare random words for insertion
	insert := make([]string, 3000)
	for i := 0; i < len(insert); i++ {
		insert[i] = fmt.Sprintf("%f", rand.Float64())
	}

	// Reset timer and run benchmark for insertion
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		n := NewNode()
		n.Insert(insert...)
	}
}

func BenchmarkTrie_Find_non_existant(b *testing.B) {

	// prepare random words for insertion
	insert := make([]string, 3000)
	for i := 0; i < len(insert); i++ {
		insert[i] = fmt.Sprintf("%f", rand.Float64())
	}
	n := NewNode()
	n.Insert(insert...)

	// Reset timer and run benchmark for finding non existing
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		n.Find("0.3213213244346546546546565465465") // does not exists
	}
}

func BenchmarkTrie_Find_existant(b *testing.B) {
	// prepare and insert random words
	insert := make([]string, 3000)
	for i := 0; i < len(insert); i++ {
		insert[i] = fmt.Sprintf("%f", rand.Float64())
	}
	n := NewNode()
	n.Insert(insert...)

	// Reset timer and run benchmark for finding existing words
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		n.Find(insert[i%3000]) // always exists !
	}
}

func BenchmarkTrie_Remove_lazy(b *testing.B) {
	// prepare and insert random words
	insert := make([]string, 3000)
	for i := 0; i < len(insert); i++ {
		insert[i] = fmt.Sprintf("%f", rand.Float64())
	}
	n := NewNode()
	n.Insert(insert...)

	// Reset timer and run benchmark for lazily removing words
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		n.Remove(insert[i%3000]) //  exists, at least until removed ...
	}
}

func BenchmarkTrie_Remove_and_Compact(b *testing.B) {
	// prepare and insert random words
	insert := make([]string, 3000)
	for i := 0; i < len(insert); i++ {
		insert[i] = fmt.Sprintf("%f", rand.Float64())
	}
	n := NewNode()
	n.Insert(insert...)

	// Reset timer and run benchmark for removing words and immediately compacting
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		n.Remove(insert[i%3000])
		n.Compact()
	}
}
"

go.trie test:
"package trie

import (
	"testing"
)

func TestTrieInsert(t *testing.T) {
	n := NewNode()

	insertWords := []string{
		"nikola",
		"tesla",
	}

	checkWords := map[string]bool{
		"thomas": false,
		"edison": false,
		"nikola": true,
	}

	n.Insert(insertWords...)
	n.verify(t, checkWords)
	n.verifySizeCapa(t, 2, 12)

}

func TestTrieInsert_substrings(t *testing.T) {
	n := NewNode()

	insertWords := []string{
		"aa",
		"aaaa",
		"aaaaa",
	}

	checkWords := map[string]bool{
		"a":       false,
		"aa":      true,
		"aaa":     false,
		"aaaa":    true,
		"aaaaa":   true,
		"aaaaaa":  false,
		"aaaaaaa": false,
	}

	n.Insert(insertWords...)
	n.verify(t, checkWords)
	n.verifySizeCapa(t, 3, 5+1)

	n.Remove("aaaa")
	checkWords["aaaa"] = false
	n.verify(t, checkWords)
	n.verifySizeCapa(t, 2, 5+1)

	if n.Compact() {
		t.Fatalf("it should not be possible to remove the node")
	}
	n.verify(t, checkWords)
	n.verifySizeCapa(t, 2, 5+1)
}

func TestTrieRemove(t *testing.T) {
	n := NewNode()

	insertWords := []string{
		"nikola",
		"tesla",
		"albert",
		"einstein",
	}

	checkWords := map[string]bool{
		"thomas":   false,
		"edison":   false,
		"nikola":   true,
		"albert":   true,
		"einstein": true,
	}

	n.Insert(insertWords...)
	n.verify(t, checkWords)
	size, capa := n.Size(), n.Capacity() // 4 words ...

	n.Remove("albert")
	checkWords["albert"] = false
	size-- // 3 words in size, but no change in capacity
	n.verify(t, checkWords)
	n.verifySizeCapa(t, size, capa)

	n.Remove("albert") // no effect since already removed
	n.verify(t, checkWords)
	n.verifySizeCapa(t, size, capa)

	n.Remove("marcel") // no effect since ,o, existent
	n.verify(t, checkWords)
	n.verifySizeCapa(t, size, capa)

	n.Remove("nikola", "tesla") // 1 word
	checkWords["nikola"] = false
	checkWords["tesla"] = false
	size -= 2 // t1 word left,  but still no change in capacity
	n.verify(t, checkWords)
	n.verifySizeCapa(t, size, capa)

	// compact the Tree
	if n.Compact() {
		t.Fatal("the Trie should not be completely removable after compaction")
	}
	if capa <= n.Capacity() {
		t.Fatal("capacity should have reduced following compaction")
	}
	capa = n.Capacity()
	n.verify(t, checkWords)
	n.verifySizeCapa(t, size, capa) // still 1 word, reduced capacity

	n.Remove("einstein")
	checkWords["einstein"] = false
	size-- // No more words
	n.verify(t, checkWords)
	n.verifySizeCapa(t, size, capa) // no words, but still have some nodes left capacity

	if !n.Compact() {
		t.Fatal("the root node of an empty Trie should be marked as removable after compaction")
	}
	n.verifySizeCapa(t, 0, 1) // no words, only the root node left
}

// --------------- helper functions ---------------------------

// verify if provided words are present
func (n *Node) verify(t *testing.T, checkWords map[string]bool) {
	for k, v := range checkWords {
		ok := n.Find(k)
		if ok != v {
			t.Fatalf(
				"%q is %s supposed to be in the Trie.",
				k,
				map[bool]string{true: "", false: "NOT "}[v],
			)
		}
		// t.Logf(
		// 	"\"%s\" is %sin the Trie.",
		// 	k,
		// 	map[bool]string{true: "", false: "NOT "}[ok],
		// )
	}
}

// verify expected size and capacity
func (n *Node) verifySizeCapa(t *testing.T, expectedSize, expectedCapacity int) {
	if got := n.Size(); got != expectedSize {
		t.Fatalf("Expected Size was %d but got %d", expectedSize, got)
	}
	if got := n.Capacity(); got != expectedCapacity {
		t.Fatalf("Expected Capacity was %d but got %d", expectedCapacity, got)
	}
}
"
